import React, { Component } from 'react';
import { withRouter, Link } from 'react-router-dom';
import L from 'leaflet';
import { Map, TileLayer, Marker, Popup } from "react-leaflet";

class Maps extends Component {

    componentDidMount = async () => {
        // // Creating map options
        // var mapOptions = {
        // center: [17.385044, 78.486671],
        // zoom: 10
        // }

        // // Creating a map object
        // var map = new L.map('map', mapOptions);

        // // Creating a Layer object
        // var layer = new L.TileLayer('http://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png');

        // // Adding layer to the map
        // map.addLayer(layer);
    }

    render () {
        const buildingData = this.props.buildingData
        let iconBelumValid = L.icon({
            iconRetinaUrl: <i className="material-icons">location_on</i>,
            iconUrl: <i className="material-icons">location_on</i>,
            iconSize: [45, 45], // size of the icon
            shadowSize: [50, 64], // size of the shadow
            iconAnchor: [22, 45], // point of the icon which will correspond to marker's location
            shadowAnchor: [4, 62], // the same for the shadow
            popupAnchor: [0, -43] // point from which the popup should open relative to the iconAnchor
          });
        return (
            <React.Fragment>
                {/* <div id = "map"> */}
                <Map center={[buildingData[0].latitude*1, buildingData[0].longitude*1]} zoom={12} className="map">
                    <TileLayer
                    attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    {buildingData.map(building => (
                    <Marker position={[building.latitude*1, building.longitude*1]}>
                    <Popup>
                        {building.description}
                    </Popup>
                    </Marker>

                    ))}
                </Map>
                {/* </div> */}
            </React.Fragment>    
        );
    }
}

export default Maps;
