import React, { Component } from 'react';
import axios from 'axios';
import BuildingList from '../components/buildingList'
import '../styles/css/home.css'

class Index extends Component {

    state = {
        buildingDataAll : [],
        buildingData : [],
        page : 1
    }

    componentDidMount = async () => {
        const building = {
			method: 'get',
			url: 'http://0.0.0.0:5000/building',
			headers: {
				'Content-Type': 'application/json',
			}
        };

        const buildingRes = await axios(building)
        this.setState({buildingDataAll : buildingRes.data})
        this.setState({buildingData : buildingRes.data.slice(0,4)})
        console.warn(this.state.buildingDataAll)
    }

    pagination = async (event) => {
        const pageNumber = await this.state.page

        if (event.target.name === 'prev') {
            this.setState({page : pageNumber-1})
        } else {
            this.setState({page : pageNumber+1})
        }

        const buildingIndex = await this.state.page*4
        const buildingData = await this.state.buildingDataAll.slice(buildingIndex-4, buildingIndex)
        this.setState({buildingData : buildingData})

        await console.warn('buildingData', this.state.buildingData)
    }

    render () {
        return (
            <React.Fragment>
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-md-6 col-12">
                            <div className="row px-1">
                                {this.state.buildingDataAll.map(item=>
                                    <BuildingList building={item}/>
                                )}
                            </div>
                            <div className="pagination mt-5 mb-3 justify-content-center">
                                <button type='button' className='btn btn-danger mr-3' name='prev'>Previous</button>
                                <button type='button' className='btn btn-danger' name='next'>Next</button>
                            </div>
                        </div>
                        <div className="col-md-6 col-12">
                            <div className="border" style={{height:'500px'}}></div>
                        </div>
                    </div>
                </div>
            </React.Fragment>    
        );
    }
}

export default Index;
