import React, { Component } from 'react';
import axios from 'axios';
import '../styles/css/detail.css'

class Index extends Component {

    state = {
        buildingData : {}
    }

    componentDidMount = async () => {
        console.warn(this.props)
        const id = this.props.match.params.id
        const building = {
			method: 'get',
			url: 'http://0.0.0.0:5000/building/'+id,
			headers: {
				'Content-Type': 'application/json',
			}
        };

        const buildingRes = await axios(building)
        this.setState({buildingData : buildingRes.data})
    }

    render () {
        const buildingData = this.state.buildingData
        return (
            <React.Fragment>
                <div className="detail__image">
                    <img src={buildingData.image} alt=""/>
                </div>
                <div className="detail__content">
                    <div className="container">
                        <div className="detail__content--title">
                            {buildingData.name}
                        </div>
                        <div className="row">
                            <div className="col-md-6 col-sm-7 col-12">
                                <div className="detail__content--desc">
                                    <h5 className="my-3">Description</h5>
                                    <p>
                                        {buildingData.description}
                                    </p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </React.Fragment>    
        );
    }
}

export default Index;
